#!/bin/bash
whois $1 | awk '/Registrant/ {print "Registrant Name," $3 " " $4} /Registrant/ && /Organization/ {print "Registrant Organization," $3 " " $4} /Registrant/ && /Street/ {print "Registrant Street," $3 " " $4 " "} /Registrant/ && /City/ {print "Registrant City," $3 " " $4} /Registrant/ && /State/Province/ {print "Registrant State/Province," $3} /Registrant/ && /Postal/ {print "Registrant Postal Code," $3} /Registrant/ && /Country/ {print "Registrant Country," $3} /Registrant/ && /Phone/ {print "Registrant Phone," $3} /Registrant/ && /Fax/ {print "Registrant Fax," $3} /Registrant/ && /Email/ {print "Registrant Email," $3} /Admin/ {print "Admin Name," $3 " " $4} /Admin/ && /Organization/ {print "Admin Organization," $3 " " $4} /Admin/ && /Street/ {print "Admin Street," $3 " " $4 " "} /Admin/ && /City/ {print "Admin City," $3 " " $4} /Admin/ && /State/Province/ {print "Admin State/Province," $3} /Admin/ && /Postal/ {print "Admin Postal Code," $3} /Admin/ && /Country/ {print "Admin Country," $3} /Admin/ && /Phone/ {print "Admin Phone," $3} /Admin/ && /Fax/ {print "Admin Fax," $3} /Admin/ && /Email/ {print "Admin Email," $3} /Tech/ {print "Tech Name," $3 " " $4} /Tech/ && /Organization/ {print "Tech Organization," $3 " " $4} /Tech/ && /Street/ {print "Tech Street," $3 " " $4 " "} /Tech/ && /City/ {print "Tech City," $3 " " $4} /Tech/ && /State/Province/ {print "Tech State/Province," $3} /Tech/ && /Postal/ {print "Tech Postal Code," $3} /Tech/ && /Country/ {print "Tech Country," $3} /Tech/ && /Phone/ {print "Tech Phone," $3} /Tech/ && /Fax/ {print "Tech Fax," $3} /Tech/ && /Email/ {print "Tech Email," $3}' > "$1.csv"
